import React, {useState, useEffect} from 'react';
import { View, Text, Button, TouchableOpacity, StyleSheet, Image, FlatList } from 'react-native'
import { images, COLORS, FONTS, SIZES } from '../../constants';


const View1 = (props) => {
  const [items, setItems] = React.useState([])
  const [user, setUser] = React.useState([])


  function APIItems() {
    if(props.userData == null) setUser([]) 
    else setUser(props.userData)
    console.log(user)
    fetch(props.apiURI + '/items' + `/${user.userID}`, {
      method: 'GET',
    })
      .then(response => {
        if (response.ok == false) {
          throw new Error("HTTP Code " + response.status + " - " + JSON.stringify(response.json()));
        }
        return response.json();
      })
      .then(json => {
        setItems(json)
        console.log(json)
      })
      .catch(error => {
        console.log("Error message:")
        console.log(error.message)
      });
  }

  useEffect(() => {
    APIItems();
  }, []);

  function renderItem(item, index) {
    return (
      <TouchableOpacity
        style={{ flex: 1, flexDirection: 'row' }}
      >
        <View style={{ flex: 1}}>
          <Image
            source={item.images}
            resizeMode="contain"
            style={{
              width: 130,
              height: 100,
            }}
          />
        </View>
        <View style={{}}>
        <TouchableOpacity>
        <Text>
          <Icon/>
        </Text>
      </TouchableOpacity>
          <Text style={{ color: COLORS.black, ...FONTS.body3 }}>{item.title}</Text>
          <Text style={{ color: COLORS.lightGray, ...FONTS.body5 }}>{item.description}</Text>
          <Text style={{ ...FONTS.h3 }}>{item.price}$</Text>
        </View>
      </TouchableOpacity>
    )
  }

  return (
    <View style={{ flex: 1 }}>
      <Text style={{ fontSize: 40, fontWeight: '500' }}>Cpanel</Text>
      <Text style={{ fontSize: 20, fontWeight: '300' }}>Welcome back, {user.name}</Text>
      <Button
        title="POST A NEW ITEM"
        onPress={() => props.navigation.navigate('Todos')}
      />
      <Button
        title="Logout"
        onPress={props.onLogout}
      />

      <View>
                    <FlatList
                        showsVerticalScrollIndicator={false}
                        data={items}
                        keyExtractor={item => item.id.toString()}
                        renderItem={({ item, index }) => renderItem(item, index)}
                    />
      </View>
    </View>

  )
}



export default View1
