import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, Button, TextInput, TouchableHighlight } from 'react-native'

const EditItem = (props) => {

    const [title, setTitle] = React.useState("")
    const [desc, setDesc] = React.useState("")
    const [category, setCategory] = React.useState("")
    const [country, setCountry] = React.useState("")
    const [city, setCity] = React.useState("")
    const [image, setIMG] = React.useState("")
    const [price, setPrice] = React.useState("")
    const [deliveryType, setDType] = React.useState("")


    function onEdit() {
        fetch(props.apiURI + '/users', {
            method: 'POST',
            body: JSON.stringify({
                username: username,
                name: email,
                password: password
            }),
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        })
            .then(response => {
                if (response.ok == false) {
                    throw new Error("HTTP Code " + response.status + " - " + JSON.stringify(response.json()));
                }
                return response.json();
            })
            .then(json => {
                props.navigation.reset({
                    index: 0,
                    routes: [{ name: 'SignupCompleted' }],
                })
            })
            .catch(error => {
                console.log("Error message:")
                console.log(error.message)
            });
    }

    useEffect(() => {
        console.log(props)
    }, []);
    const item = props.itemToEdit
    return (
        <View style={styles.screen}>
            <Text>Title</Text>
            <TextInput
                style={styles.input}
                value={username}
                placeholder={item.title}
                onChangeText={value => setUsername(value)}
            />
            <Text>Description</Text>
            <TextInput
                style={styles.input}
                value={email}
                placeholder={item.description}
                onChangeText={value => setEmail(value)}
            />
            <Text>Category</Text>
            <TextInput
                style={styles.input}
                value={password}
                placeholder="password"
                onChangeText={value => setPassword(value)}
            />
            <Text>Location</Text>
            <TextInput
                style={styles.input}
                value={password}
                placeholder={item.location.country}
                onChangeText={value => setPassword(value)}
            />
            <TextInput
                style={styles.input}
                value={password}
                placeholder={item.location.city}
                onChangeText={value => setPassword(value)}
            />
            <Text>Images link</Text>
            <TextInput
                style={styles.input}
                value={password}
                placeholder='Link to images'
                onChangeText={value => setPassword(value)}
            />
            <Text>Price</Text>
            <TextInput
                style={styles.input}
                value={password}
                placeholder='$'
                onChangeText={value => setPassword(value)}
            />
            <Text>Delivery Type</Text>
            <TextInput
                style={styles.input}
                value={password}
                placeholder={item.deliveryType}
                onChangeText={value => setPassword(value)}
            />
            <TouchableHighlight onPress={() => onEdit()}>
                <View style={styles.primaryButton}>
                    <Text style={styles.primaryButtonText}>Sign up</Text>
                </View>
            </TouchableHighlight>
            <Button
                title="Cancel"
                color="#000000"
                onPress={
                    () => props.navigation.reset({
                        index: 0,
                        routes: [{ name: 'TodoApp' }],
                    })
                } />
        </View>
    )
}

const styles = StyleSheet.create({
    screen: {
        backgroundColor: 'rgb(227, 178, 0)',
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        marginTop: -10
    },
    text: {
        fontSize: 20,
        color: 'white'
    },
    input: {
        borderWidth: 1,
        borderRadius: 20,
        height: 32,
        width: '90%',
        backgroundColor: 'white',
        textAlign: 'center',
        fontSize: 16,
        marginTop: 5,
        marginBottom: 20
    },
    primaryButton: {
        backgroundColor: 'rgb(0, 153, 51)',
        height: 40,
        width: 150,
        justifyContent: 'center',
        alignItems: 'center',
        borderColor: 'black',
        borderWidth: 2,
        marginTop: 5,
        marginBottom: 5
    },
    primaryButtonText: {
        color: 'white',
        fontSize: 20
    }
});


export default EditItem
